import { StrictMode } from "react";
import { createRoot } from "react-dom/client";
import "./styles.css";

function App() {
  return (
    <div className="card">
      <Avatar />
      <div className="data">
        <Intro />
        {/* Should contain one Skill component
        for each web dev skill that you have,
        customized with props */}
        <SkillList />
      </div>
    </div>
  );
}

function Avatar() {
  return (
    <img className="avatar" src="/avatar/ricky_avatar.png" alt="Ricky Tan" />
  );
}

function SkillList() {
  return (
    <div className="skills">
      <Skill skill="HTML" textColor="white" backgroundColor="tomato" />
      <Skill skill="CSS" textColor="white" backgroundColor="dodgerblue" />
      <Skill skill="JavaScript" textColor="white" backgroundColor="gold" />
      <Skill skill="C# .NET" textColor="white" backgroundColor="darkblue" />
      <Skill
        skill="Project Management"
        textColor="white"
        backgroundColor="green"
      />
      <Skill skill="AWS" textColor="white" backgroundColor="darkGray" />
    </div>
  );
}

function Skill(props) {
  const { skill, textColor, backgroundColor } = props;
  const style = {
    color: textColor || "#333", // Default text color
    backgroundColor: backgroundColor || "#f0f8ff", // Default background color
  };
  return (
    <span className="skill" style={style}>
      {skill}
    </span>
  );
}

function Intro() {
  return (
    <div>
      <h1>Ricky Tan</h1>
      <p>
        I'm an accomplished software developer, certified PMP and AWS Solutions
        Architect, with 18+ years in designing and deploying scalable .NET,
        Java, and cloud-based solutions. Expertise spans telecommunications,
        banking, insurance, and government sectors, with a proven record of
        enhancing operational efficiency through automation, database
        management, and enterprise-level software integration.
      </p>
    </div>
  );
}

const rootElement = document.getElementById("root");
const root = createRoot(rootElement);

root.render(
  <StrictMode>
    <App />
  </StrictMode>
);
