import { useState } from "react";
import "./App.css";

export default function App() {
  return (
    <div className="App">
      <Counter />
    </div>
  );
}

function Step() {
  const [step, setStep] = useState(0);

  function handleDecreaseStep() {
    if (step > 0) setStep((s) => s - 1);
  }

  function handleIncreaseStep() {
    setStep((s) => s + 1);
  }

  return (
    <div className="step">
      <button onClick={handleDecreaseStep}>-</button>
      <span>Step: {step}</span>
      <button onClick={handleIncreaseStep}>+</button>
    </div>
  );
}

function Counter() {
  const [step, setStep] = useState(0);
  const [counter, setCounter] = useState(0);

  function handleDecreaseStep() {
    if (step > 0) setStep((s) => s - 1);
  }

  function handleIncreaseStep() {
    setStep((s) => s + 1);
  }

  function handleDecreaseCounter() {
    setCounter((c) => c - 1);
  }

  function handleIncreaseCounter() {
    setCounter((c) => c + 1);
  }

  return (
    <>
      <div className="step">
        <button onClick={handleDecreaseStep}>-</button>
        <span>Step: {step}</span>
        <button onClick={handleIncreaseStep}>+</button>
      </div>
      <div className="counter">
        <button onClick={handleDecreaseCounter}>-</button>
        <span>Count: {counter}</span>
        <button onClick={handleIncreaseCounter}>+</button>
      </div>
      <br />
      <DateCounter />
    </>
  );
}

function DateCounter({ dayCount }) {
  return (
    <div className="date-counter">
      <span>Today is {new Date().toDateString()}</span>
    </div>
  );
}
