import "./App.css";

export default function App() {
  return (
    <div className="App">
      <Step />
      <Counter />
      <br />
      <DateCounter />
    </div>
  );
}

function Step() {
  return (
    <div className="steps">
      <button>-</button>
      <span>TEST Step</span>
      <button>+</button>
    </div>
  );
}

function Counter() {
  return (
    <div>
      <button>-</button>
      <span>TEST Counter</span>
      <button>+</button>
    </div>
  );
}

function DateCounter() {
  return <div>Today is {new Date().toDateString()}</div>;
}
