const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  return (
    <div className="app">
      <div className="sidebar">
        <h1> My Friends </h1>
        <FriendList />

        <FormAddFriend />
        <Button>Add Friend</Button>
      </div>
    </div>
  );
}

function FriendList() {
  const friends = initialFriends;
  return (
    <ul>
      {friends.map((friend) => (
        <Friend key={friend.id} friend={friend} />
      ))}
    </ul>
  );
}

function Friend({ friend }) {
  return (
    <li>
      <img src={friend.image} alt={friend.name} />
      <h3>{friend.name}</h3>

      {friend.balance < 0 ? (
        <p className="red">
          You owe {friend.name} RM ${Math.abs(friend.balance)}
        </p>
      ) : friend.balance === 0 ? (
        <p className="even">You and {friend.name} are even</p>
      ) : (
        <p className="green">
          {friend.name} owes you RM ${friend.balance}
        </p>
      )}
      <Button>Select</Button>
    </li>
  );
}

function FormAddFriend() {
  return (
    <form className="form-add-friend">
      <label>ðŸ‘« Friend Name</label>
      <input type="text" placeholder="Name" />
      <label>ðŸ“¸ Image URL</label>
      <input type="text" placeholder="Image URL" />
      <Button>Add</Button>
    </form>
  );
}

function Button({ children }) {
  return <button className="button">{children}</button>;
}

function FormSplitBill() {
  return (
    <form className="form-split-bill">
      <h2>Split a bill with your friends</h2>
      <label>Bill Value</label>
      <input type="text" placeholder="Bill Value" />
      <label>Your Expense</label>
      <input type="text" placeholder="Your Expense" />
      <label>Friend's Name</label>
      <input type="text" placeholder="Friend's Name" />
      <label>Friend's Expense</label>
      <input type="text" placeholder="Friend's Expense" />
      <Button>Split Bill</Button>
    </form>
  );
}
