const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  return (
    <div className="app">
      <h1>My friends</h1>
      <ul>
        {initialFriends.map((friend) => (
          <li key={friend.id}>
            <h3>{friend.name}</h3>
            <p>{friend.balance}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}
