const containerStyle = {
  display: "flex",
  alignItems: "center",
  gap: "16px",
  color: "#e4b600",
};

const starStyle = {
  display: "flex",
  gap: "4px",
  fontSize: "24px",
};

const textStyle = {
  lineHeight: "1px",
  margin: "0",
};
export default function StarRating({ maxRating }) {
  return (
    <div style={containerStyle}>
      <div style={starStyle}>
        {Array.from({ length: maxRating }, (_, i) => (
          <span key={i}>&#9733;</span>
        ))}
      </div>
      <p style={textStyle}>10</p>
    </div>
  );
}
