import { useState } from "react";

export default function App() {
  const [bill, setBill] = useState(0);
  const [myServiceRate, setMyServiceRate] = useState(0);
  const [friendServiceRate, setFriendServiceRate] = useState(0);

  function handleSetBill(bill) {
    setBill(bill);
  }

  function handleSetServiceRate(serviceRate) {
    setServiceRate(serviceRate);
  }

  return (
    <div className="App">
      <div>
        <h1>Tip Calculator</h1>
      </div>
      <section>
        <div>
          <span>How much was the bill?</span>
          <Bill bill={bill} onSetBill={handleSetBill} />
        </div>
        <div>
          <span>How did you like the service?</span>
        </div>
        <div>
          <span>How did your friend like the service?</span>
        </div>
      </section>
    </div>
  );
}

function Bill({ bill, onSetBill }) {
  return (
    <input
      type="number"
      value={bill}
      onChange={(e) => onSetBill(Number(e.target.value))}
    />
  );
}

function ServiceRating({ serviceRate, setServiceRate }) {
  return (
    <div>
      <select
        selectedValue={serviceRate}
        onChange={(e) => setServiceRate(Number(e.target.value))}
      >
        <option value="0">Dissatisfied ({serviceRate}%)</option>
        <option value="5">Satisfied ({serviceRate}%)</option>
        <option value="10">It was good ({serviceRate}%)</option>
        <option value="20">Absolutely amazing! ({serviceRate}%)</option>
      </select>
    </div>
  );
}
